    public function storeProduct($product)
    {
        $savedProduct = Product::where('asin', '=',$product->asin)->with('images')->first();

        $myProduct = [
            'asin' => $product->asin,
            'title' => $product->title,
            'price' => $product->price,
            'url'=> $product->price,
            'rating'=> $product->price,
            'item_available'=> $product->item_available,
            'discount'=> $product->discount ?? 0,
            'total_reviews'=> $product->reviews['total_reviews']
        ];

        if (empty($savedProduct)){
            $savedProduct = Product::create($myProduct);
        }

        $images = [];
        if (isset($product->images)){
            $images[]['link'] = $product->images[0];
        }

        foreach ($images as $image) {
            $savedProduct->images()->updateOrCreate($image);
        }
        return $savedProduct;
    }
